import { routes } from "../../../router";

import { login, getInfoUser } from "../api";
import {
  getToken,
  setToken,
  removeToken,
  // setUserName,
  getUserName,
  setUserInfo,
  getUserInfo,
} from "../../../util/session";

import { createHelpers } from "vuex-map-fields";
const { getDataFields, updateDataField } = createHelpers({
  getterType: "getDataFields",
  mutationType: "updateDataField",
});

function hasPermission(group, route) {
  if (route.meta && route.meta.group) {
    return route.meta.group.some((g) => g == group);
  } else {
    return true;
  }
}

export function filterAsyncRoutes(authBaseRoutes, group) {
  const res = [];

  authBaseRoutes.forEach((route) => {
    const tmp = { ...route };

    if (hasPermission(group, tmp)) {
      if (tmp.children) {
        tmp.children = filterAsyncRoutes(tmp.children, group);
      }
      res.push(tmp);
    }
  });
  return res;
}

export default {
  namespaced: true,
  state: {
    routes: [],
    userData: null,
    tokenId: getToken(),
    roles: [],
    group: [],
    userInfo: {},
    user_Name: "",
  },

  getters: {
    getDataFields,
    isAuthenticated(state) {
      return state.tokenId !== null;
    },
    getToken(state) {
      return state.tokenId;
    },
    getUserName(state) {
      return state.user_Name;
    },

    getRole(state) {
      return state.roles ?? [];
    },

    getRoutes(state) {
      return state.routes;
    },
    getUserData(state) {
      return state.userData;
    },
    getUserInfo(state) {
      return state.userInfo;
    },
  },

  mutations: {
    updateDataField,
    SET_TOKEN: (state, token) => {
      state.tokenId = token;
    },
    SET_ROLES: (state, roles) => {
      state.roles = roles;
    },
     
    SET_USER_INFO(state, userinfo) {
      state.userInfo = userinfo;
    },

    authUser(state, userData) {
      state.tokenId = userData.token;
    },

    SET_UserName(state, userName) {
      state.user_Name = userName;
    },

    setUserData(state, userData) {
      state.userData = userData;
    },

    SET_ROUTES(state) {
      state.routes = JSON.parse(JSON.stringify(routes));
    },
    clearAuthData(state) {
      state.tokenId = null;
      state.routes = [];
      state.userData = null;
    },
  },

  actions: {
    set_UserName({ commit }) {
      commit("SET_UserName", getUserName());
    },

    set_routers({ commit }) {
      commit("SET_ROUTES");
    },
    login({ commit }, authData) {
      const { nameOrNumber, password } = authData;
      return new Promise((resolve, reject) => {
        login({ nameOrNumber: nameOrNumber, password: password })
          .then((response) => {

            console.log(response.result);

            commit("setUserData", response.result);
            commit("authUser", response.result.accessToken);
            commit("SET_ROLES", response.result.permisstions);
            commit("SET_USER_INFO", response.result);
            setToken(response.result.accessToken);
            // setUserName(response.result);
            setUserInfo(response.result);

            resolve();
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    getUserInfoSesstion({ commit }) {
      getUserInfo().then((response) => {
        commit("SET_USER_INFO", response);
        commit("SET_ROLES", response?.permisstions);
      });
    },
    getInfo({ commit }) {
      return new Promise((resolve, reject) => {
        getInfoUser()
          .then((response) => {
            const { result } = response;

            if (!result) {
              reject("Verification failed, please Login again.");
            }

            // const { permisstions, moduleName} = result
            const { permisstions } = result;

            // roles must be a non-empty array
            if (!permisstions || permisstions.length <= 0) {
              reject("getInfo: roles must be a non-null array!");
            }

            commit("SET_ROLES", permisstions);
            commit("SET_USER_INFO", result);

            resolve(result);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    logout({ commit }) {
      commit("clearAuthData");
      removeToken();
      // router.replace("/login");
    },
    resetToken({ commit }) {
      commit("clearAuthData");
      removeToken();
    },
     
    tryAutoLogin({ commit }) {
      const token = getToken();
      if (!token) {
        return Promise.reject("no token");
      }
      commit("authUser", {
        token: token,
      });
      return Promise.resolve(true);
    },

    // tryAutoLoginAndCheckSelectedKyc({ commit }) {
    //   console.log(commit);
    //   return new Promise((resolve, reject) => {
    //     getUserInfo()
    //       .then((response) => {
    //         if (response && response.kycTypeId ) {
    //           resolve(true);
    //         }
    //         reject(false);
    //       })
    //       .catch((error) => {
    //         reject(error);
    //       });
    //   });
   
    //   // const info = getUserInfo().then();
    //   // console.log(info);
      
    //   // alert("true")

    //   // return Promise.resolve(true);
    // },
  },
};
