import { getOrderItems, deleteOrderItem } from "../apis/index.js";

import { createHelpers } from "vuex-map-fields";
const { getDataFields, updateDataField } = createHelpers({
  getterType: "getDataFields",
  mutationType: "updateDataField",
});
export default {
  namespaced: true,
  state: {
    orderItems: [],
    pageCount: 0,
  },

  getters: {
    getDataFields,
  },

  mutations: {
    updateDataField,

    SET_DATA_ORDER_ITEMS(state, orderItems) {
      state.orderItems = orderItems;
    },

    SET_PAGE_COUNT(state, pageCount) {
      state.pageCount = pageCount;
    },
  },

  actions: {
    GetOrderItems({ commit }, filter) {
      return new Promise((resolve, reject) => {
        getOrderItems(filter)
          .then((response) => {
            commit("SET_DATA_ORDER_ITEMS", response.result.data);
            commit("SET_PAGE_COUNT", response.result.pageCount);

            resolve(response);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    // UpdateOrderRequest({ commit, state }) {
    //   return new Promise((resolve, reject) => {
    //     updateOrderRequest(state.editedItem)
    //       .then((response) => {
    //         commit("RESET_DATA");
    //         resolve(response.messages[0]);
    //       })
    //       .catch((error) => {
    //         reject(error);
    //       });
    //   });
    // },

    DeleteOrderItem({ commit }, item) {
      return new Promise((resolve, reject) => {
        deleteOrderItem(item)
          .then((response) => {
            console.log(commit);
            resolve(response.messages[0]);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },
  },
};
