import * as enums from "./enum.js";

export function OrderRequestTypeToTextPublic(orderRequestType) {
  let message = "";
  switch (orderRequestType) {
    case enums.OrderRequestType.Individual:
      message = "أفــراد";
      break;
    case enums.OrderRequestType.Companies:
      message = "شركــات";
      break;
    case enums.OrderRequestType.Certified:
      message = "مصدقة";
      break;
    default:
      break;
  }

  return message;
}

export function OrderItemStateToTextPublic(item) {
  let messge = "";
  switch (item) {
    case enums.OrderItemState.Success:
      messge = "تم طلب";
      break;
    case enums.OrderItemState.IsSuspended:
      messge = "حساب مجمد";
      break;
    default:
      break;
  }

  return messge;
}

export function OrderRequestStateToTextPublic(orderRequestState) {
  let messge = "";
  switch (orderRequestState) {
    case enums.OrderRequestState.Process:
      messge = "تحت الإدخال";
      break;
    case enums.OrderRequestState.Pinding:
      messge = "تم الطلب";
      break;
    case enums.OrderRequestState.GeneretedCode:
      messge = "جاري إصدار رقم إشاري";
      break;
    case enums.OrderRequestState.SendRequest:
      messge = "تم إرسال مركز الطباعة";
      break;
    default:
      break;
  }
  return messge;
}

export function ChangeColorOrderRequestTypePublic(orderRequestType) {
  let color = "";
  switch (orderRequestType) {
    case enums.OrderRequestType.Individual:
      color = "indigo";
      break;
    case enums.OrderRequestType.Companies:
      color = "green";
      break;
    case enums.OrderRequestType.Certified:
      color = "orange";
      break;
    default:
      break;
  }
  return color;
}

export function ValidateAccountCountWithMinBranchCountPublic(item) {
  return item.accountCount < item.minOrderItemBranch;
}

export function ValidateOrderRequestStatePublic(item) {
  return enums.OrderRequestState.Process != item.orderRequestState;
}


export function OrderRequestStateColorPublic(orderRequestState){
  let color = "";
  switch (orderRequestState) {
    case enums.OrderRequestState.Process:
      color = "#E91D62";
      break;
    case enums.OrderRequestState.Pinding:
      color = "green";
      break;
    case enums.OrderRequestState.GeneretedCode:
      color = "orange";
      break;
    case enums.OrderRequestState.SendRequest:
      color = "indigo";
      break;
    default:
      break;
  }
 
  return color;
  // 
}
