import Authrequest from "../../../util/baseurl";

export function login(data) {
  return Authrequest({
    url: "/LoginUser/AuthUser",
    method: "post",
    data,
  });
}

export function getInfoUser(data) {
  return Authrequest({
    url: "/LoginUser/GetUserInfo",
    method: "Get",
    params: data,
  });
}


export function resetPasswordUser(data) {
  return Authrequest({
    url: "/LoginUser/ResetPasswordUser",
    method: "post",
    data,
  });
}

export function confirmResetPasswordUser(data) {
  return Authrequest({
    url: "/LoginUser/ConfirmResetPasswordUser",
    method: "post",
    data,
  });
}
