import { getBranchs, getActiveBranchs, insertBranch } from "../API/Index.js";

import { createHelpers } from "vuex-map-fields";
const { getDataFields, updateDataField } = createHelpers({
  getterType: "getDataFields",
  mutationType: "updateDataField",
});
export default {
  namespaced: true,
  state: {
    branchs: [],
    activeBranchs: [],
    pageCount: 0,

    editedItem: {
      id: null,
      name: null,
      bankNo: null,
      bankRegions: [],
    },

    defaultItem: {
      id: null,
      name: null,
      bankNo: null,
      bankRegions: [],
    },
  },

  getters: {
    getDataFields,
  },

  mutations: {
    updateDataField,

    SET_DATA_BRANCHS(state, items) {
      state.branchs = items;
    },

    SET_DATA_ACTIVE_BRANCHS(state, items) {
      state.activeBranchs = items;
    },

    SET_PAGE_COUNT(state, pageCount) {
      state.pageCount = pageCount;
    },

    RESET_DATA(state) {
      state.editedItem = Object.assign({}, state.defaultItem);
    },
    SET_ITEM_BRANCH(state, item) {
      state.editedItem.id = item.bankId;
      state.editedItem.bankNo = item.bankNo;
      state.editedItem.name = item.name;
    },
  },

  actions: {
    ClearDataItem({ commit }) {
      commit("RESET_DATA");
    },

    SetDataUpdate({ commit }, item) {
      commit("SET_ITEM_BRANCH", item);
    },

    GetBranchs({ commit }, filter) {
      return new Promise((resolve, reject) => {
        getBranchs(filter)
          .then((response) => {
            commit("SET_DATA_BRANCHS", response.result.data);
            commit("SET_PAGE_COUNT", response.result.pageCount);

            resolve(response);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    GetActiveBranchs({ commit }, filter) {
      return new Promise((resolve, reject) => {
        getActiveBranchs(filter)
          .then((response) => {
            commit("SET_DATA_ACTIVE_BRANCHS", response.result);
            resolve(response);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    InsertBranch({ commit, state }) {
      const data = {
        name: state.editedItem.name,
        bankNo: state.editedItem.bankNo,
         
      };

      return new Promise((resolve, reject) => {
        insertBranch(data)
          .then((response) => {
            commit("RESET_DATA");
            resolve(response.messages[0]);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

