export default {
  data() {
    return {
      show: false,
      message: "",
      color: "",
      colorDef: "#261f46"
    };
  },
  created() {
    this.$store.subscribe((mutation, state) => {
      if (mutation.type === "showMessage") {
        this.message = state.notificationText;
        this.color = state.notificationColor;
        this.show = true;
      }
    });
  }
};
