import requestAPI from "../../../util/baseUrlCustomer";

export function getBranchs(param) {
  return requestAPI({
    url: "/Branch/GetBranchs",
    method: "Get",
    params: param,
  });
}

export function getActiveBranchs(param) {
  return requestAPI({
    url: "/Branch/GetActiveBranchs",
    method: "Get",
    params: param,
  });
}

export function getBranchSetting(param) {
  return requestAPI({
    url: "/Branch/GetBranchSetting",
    method: "Get",
    params: param,
  });
}
export function getBranchWorkTime(param) {
  return requestAPI({
    url: "/Branch/GetBranchWorkTime",
    method: "Get",
    params: param,
  });
}

export function insertBranch(data) {
  return requestAPI({
    url: "/Branch/InsertBranch",
    method: "Post",
    data,
  });
}

export function insertBranchList(data) {
  return requestAPI({
    url: "/Branch/InsertBranchList",
    method: "Post",
    data,
  });
}

export function updateBranch(data) {
  return requestAPI({
    url: "/Branch/UpdateBranch",
    method: "Put",
    data,
  });
}

export function updateBranchSetting(data) {
  return requestAPI({
    url: "/Branch/UpdateBranchSetting",
    method: "Put",
    data,
  });
}

export function updateBranchWorkTime(data) {
  return requestAPI({
    url: "/Branch/UpdateBranchWorkTime",
    method: "Put",
    data,
  });
}

export function activationBranchWorkTime(item) {
  return requestAPI({
    url: "/Branch/ActivationBranchWorkTime",
    method: "Put",
    params: item,
  });
}

export function activationBranch(item) {
  return requestAPI({
    url: "/Branch/ActivationBranch",
    method: "Put",
    params: item,
  });
}

export function deleteBranch(item) {
  return requestAPI({
    url: "/Branch/DeleteBranch",
    method: "Delete",
    params: item,
  });
}
