import requestAPI from "../../../util/baseUrlCustomer";

export function getReasonRefuses(param) {
  return requestAPI({
    url: "/ReasonRefuses/GetReasonRefuses",
    method: "Get",
    params: param,
  });
}
export function getActiveReasonRefuses(param) {
  return requestAPI({
    url: "/ReasonRefuses/GetActiveReasonRefuses",
    method: "Get",
    params: param,
  });
}
export function insertReasonRefuse(data) {
  return requestAPI({
    url: "/ReasonRefuses/InsertReasonRefuse",
    method: "Post",
    data,
  });
}
export function updateReasonRefuse(data) {
  return requestAPI({
    url: "/ReasonRefuses/UpdateReasonRefuse",
    method: "Put",
    data,
  });
}

export function activationReasonRefuse(item) {
  return requestAPI({
    url: "/ReasonRefuses/ActivationReasonRefuse",
    method: "Put",
    params: item,
  });
}

export function deleteReasonRefuse(item) {
  return requestAPI({
    url: "/ReasonRefuses/DeleteReasonRefuse",
    method: "Delete",
    params: item,
  });
}
