import {
  getBranchs,
  getActiveBranchs,
  insertBranch,
  updateBranch,
} from "../API/Index.js";

import { createHelpers } from "vuex-map-fields";
const { getDataFields, updateDataField } = createHelpers({
  getterType: "getDataFields",
  mutationType: "updateDataField",
});
export default {
  namespaced: true,
  state: {
    branchs: [],
    activeBranchs: [],
    pageCount: 0,

    editedItem: {
      id: null,
      name: null,
      branchNo: null,
      branchRegionId: null,
      lastSerialCertified: 0,
      lastSerial: 0,
      lastCountChekBook: 0,
      branchSetting: {
        accountChekBook: null,
        companyFrom: 1,
        companyTo: 1000,
        certifiedFrom: 1,
        certifiedTo: 1000,
        individualFrom: 1,
        individualTo: 1000,
        individualRequestAccountDay: true,
        individualQuentityOfDay: 1,
      },
    },

    defaultItem: {
      id: null,
      name: null,
      branchNo: null,
      branchRegionId: null,
      lastSerialCertified: 0,
      lastSerial: 0,
      lastCountChekBook: 0,
      branchSetting: {
        accountChekBook: null,
        companyFrom: 1,
        companyTo: 1000,
        certifiedFrom: 1,
        certifiedTo: 1000,
        individualFrom: 1,
        individualTo: 1000,
        individualRequestAccountDay: true,
        individualQuentityOfDay: 1,
      },
    },
  },

  getters: {
    getDataFields,
  },

  mutations: {
    updateDataField,

    SET_DATA_BRANCHS(state, items) {
      state.branchs = items;
    },

    SET_DATA_ACTIVE_BRANCHS(state, items) {
      state.activeBranchs = items;
    },

    SET_PAGE_COUNT(state, pageCount) {
      state.pageCount = pageCount;
    },

    RESET_DATA(state) {
      state.editedItem = Object.assign({}, state.defaultItem);
      state.editedItem.branchSetting = Object.assign(
        {},
        state.defaultItem.branchSetting
      );
    },
    SET_ITEM_BRANCH(state, item) {
      state.editedItem.id = item.id;
      state.editedItem.name = item.name;
      state.editedItem.branchNo = item.branchNo;
      state.editedItem.branchRegionId = item.branchRegionId;
      state.editedItem.lastCountChekBook = item.lastCountChekBook;
      state.editedItem.lastSerialCertified = item.lastSerialCertified;
      state.editedItem.lastSerial = item.lastSerial;
    },
  },

  actions: {
    ClearDataItem({ commit }) {
      commit("RESET_DATA");
    },

    SetDataUpdate({ commit }, item) {
      commit("SET_ITEM_BRANCH", item);
    },

    GetBranchs({ commit }, filter) {
      return new Promise((resolve, reject) => {
        getBranchs(filter)
          .then((response) => {
            commit("SET_DATA_BRANCHS", response.result.data);
            commit("SET_PAGE_COUNT", response.result.pageCount);

            resolve(response);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    GetActiveBranchs({ commit }, filter) {
      return new Promise((resolve, reject) => {
        getActiveBranchs(filter)
          .then((response) => {
            commit("SET_DATA_ACTIVE_BRANCHS", response.result);
            resolve(response);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    InsertBranch({ commit, state }) {
      const data = state.editedItem;
      return new Promise((resolve, reject) => {
        insertBranch(data)
          .then((response) => {
            commit("RESET_DATA");
            resolve(response.messages[0]);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    UpdateBranch({ commit, state }) {
      const data = state.editedItem;

      return new Promise((resolve, reject) => {
        updateBranch(data)
          .then((response) => {
            commit("RESET_DATA");
            resolve(response.messages[0]);
          })
          .catch((error) => {
            reject(error);
          });
      });
    },

    //   ActivateBank({ commit }, item) {
    //     const param = {
    //       bankId: item.bankId,
    //       isActive: item.isActive,
    //     };
    //     return new Promise((resolve, reject) => {
    //       activationBank(param)
    //         .then((response) => {
    //           console.log(commit);
    //           resolve(response.messages[0]);
    //         })
    //         .catch((error) => {
    //           reject(error);
    //         });
    //     });
    //   },

    //   DeleteBank({ commit }, item) {
    //     const param = {
    //       bankId: item.bankId
    //     };
    //     return new Promise((resolve, reject) => {
    //       deleteBank(param)
    //         .then((response) => {
    //           console.log(commit);
    //           resolve(response.messages[0]);
    //         })
    //         .catch((error) => {
    //           reject(error);
    //         });
    //     });
    //   },
  },
};
