  insertBank,
  updateBank,
  activationBank,
  deleteBank,
} from "../Apis/index.js";

import { createHelpers } from "vuex-map-fields";
const { getDataFields, updateDataField } = createHelpers({
  getterType: "getDataFields",
  mutationType: "updateDataField",
});
export default {
  namespaced: true,
  state: {
    grids: [],
    editedItem: {
      id: null,
      name: null,
      bankNo: null,
      bankRegions: [],
    },

    defaultItem: {
      id: null,
      name: null,
      bankNo: null,
      bankRegions: [],
    },
  },

  getters: {
    getDataFields,
    GET_ACTIVE_BANKS(state)
    {
      return state.grids;
    }
  },

  mutations: {
    updateDataField,

    SET_DATA_BANKS(state, items) {
      state.grids = items;
